mapper.select:
select * from experiences


        type = #{type}


order by start_date desc

mapper.update:
update experiences
type = #{type}, title = #{title}, subtitle = #{subtitle}, logo_url = #{logoUrl}, content = #{content}, start_date = #{startDate}, end_date = #{endDate}, is_visible = #{isVisible}, update_time = #{updateTime}
where id = #{id}
