mapper.update:
update music
title = #{title}, artist = #{artist}, duration = #{duration}, cover_image = #{coverImage}, music_url = #{musicUrl}, lyric_url = #{lyricUrl}, has_lyric = #{hasLyric}, lyric_type = #{lyricType}, sort = #{sort}, is_visible = #{isVisible}, update_time = #{updateTime}
where id = #{id}
